mapper.select:
SELECT
*
FROM `${recordTableName}`
WHERE 1 = 1

    AND `partition_id` = #{partitionId}


    AND `offset` = #{offset}


    AND `key` = #{key}


    AND `timestamp` >= #{from}
    AND `timestamp` <= #{to}

ORDER BY `timestamp` DESC, `offset` DESC

mapper.delete:
DELETE FROM `${recordTableName}` WHERE `id` <
(
SELECT `id`
FROM
(
SELECT MAX(`id`) `id` FROM `${recordTableName}` WHERE `row_create_time` < #{dateTime}
) a
);

DELETE FROM `${recordDetailTableName}` WHERE `id` <
(
SELECT `id`
FROM
(
SELECT MAX(`id`) `id` FROM `${recordDetailTableName}` WHERE `row_create_time` < #{dateTime}
) a
);
